#!/bin/sh

VIDEO_WIDTH=66
VIDEO_HEIGHT=16

# exit if terminal is smaller than the gif
if [ $(tput cols) -lt $VIDEO_WIDTH -o $(tput lines) -lt $VIDEO_HEIGHT ]
then
  printf "\e[91mterminal is too small\nmust be at least $VIDEO_WIDTH by $VIDEO_HEIGHT cells\e[0m\n" >&2
  exit 1
fi

# hide prompt
stty -echo
printf '\e[?25l'

move_up="\e[$(expr $VIDEO_HEIGHT - 1)F"

# cleanup after exit
cleanup() {
  printf "$move_up\e[0J\e[?25h\e[0m"
  stty echo
  exit 0
}

trap cleanup INT

# moves cursor up and redraws frame
draw() {
  printf "$move_up$1"
  sleep 0.1
}

